import React from "react";
import { Button } from "./Button";
import styled from "styled-components";
import Imgtv from "../images/tab-tv.png";
import Imgtablet from "../images/tab-tablet.png";
import Imgmac from "../images/tab-macbook.png";

function TabContentTwo() {
  return (
    <TabContainer>
      <div className="tab-content">
        <div className="tab-top-content">
          <span>
            Watch TV shows and movies anyime, anywhere - personalized for you
          </span>
          <Button className="btn">try it</Button>
        </div>
        {/* Tab Bottom Content */}
        <div className="tab-bottom-content">
          {/* TV Image Container */}
          <div>
            <img src={Imgtv} alt="" />
            <h3> Watch on yout TV</h3>
            <p>
              Smart TVs, PlayStation, Xbox, Chromecast, Apple TV, Blue-ray
              players and more.
            </p>
          </div>
          {/* Tablet Image Container */}
          <div>
            <img src={Imgtablet} alt="" />
            <h3> Watch on yout TV</h3>
            <p>
              Smart TVs, PlayStation, Xbox, Chromecast, Apple TV, Blue-ray
              players and more.
            </p>
          </div>
          {/* Macbook Image Container */}
          <div>
            <img src={Imgmac} alt="" />
            <h3> Watch on yout TV</h3>
            <p>
              Smart TVs, PlayStation, Xbox, Chromecast, Apple TV, Blue-ray
              players and more.
            </p>
          </div>
        </div>
      </div>
    </TabContainer>
  );
}

//Main Tab Contain

const TabContainer = styled.div`
  background: var(--main-deep-dark);

  .tab-content {
    margin: 0 15%;
  }

  //Tab Top Content
  .tab-top-content {
    display: grid;
    grid-template-columns: repeat(12, 1fr);
    justify-content: center;
    align-items: center;
    padding: 2.5rem 0;
  }

  span {
    font-size: 1.5rem;
    grid-column: 1/8;
  }

  .btn {
    margin: 0 1.25rem;
    grid-column: 10/12;
  }

  .tab-bottom-content {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
  }

  .tab-bottom-content img {
    widht: 5rem;
    height: 18rem;
  }
`;

export default TabContentTwo;
