import React, { Component } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { Icon } from "react-icons-kit";
import { iosWorld } from "react-icons-kit/ionicons/iosWorld";
import { arrowSortedDown } from "react-icons-kit/typicons/arrowSortedDown";

class Footer extends Component {
  render() {
    return (
      <FooterContainer>
        <span style={{ marginLeft: "18%", fontSize: "1.125rem" }}>
          Questions? <Link> Call 1-877-742-1335</Link>
        </span>
        <div className="footer-columns">
          <ul>
            <li>
              <Link>FAQ</Link>
            </li>

            <li>
              <Link>Investor Relations</Link>
            </li>

            <li>
              <Link>Ways to watch</Link>
            </li>

            <li>
              <Link>Corporate Information</Link>
            </li>

            <li>
              <Link>Netflix Originals</Link>
            </li>
          </ul>

          <ul>
            <li>
              <Link>Help Center</Link>
            </li>

            <li>
              <Link>Jobs</Link>
            </li>

            <li>
              <Link>Terms of Use</Link>
            </li>

            <li>
              <Link>Contact Us</Link>
            </li>
          </ul>

          <ul>
            <li>
              <Link>Account</Link>
            </li>

            <li>
              <Link>Redeem Gift Cards</Link>
            </li>

            <li>
              <Link>Privacy</Link>
            </li>

            <li>
              <Link>Speed Test</Link>
            </li>
          </ul>

          <ul>
            <li>
              <Link>Media Center</Link>
            </li>

            <li>
              <Link>Buy Gift Cards</Link>
            </li>

            <li>
              <Link>Cookie Preferences</Link>
            </li>

            <li>
              <Link>Legal Notices</Link>
            </li>
          </ul>

          {/* Language Toggle Button */}

          <div class="dropup">
            <button class="dropbtn">
              <Icon icon={iosWorld} size={20} />
              English
              <Icon icon={arrowSortedDown} />
            </button>
            <div class="dropup-content">
              <Link>Link 1</Link>
              <Link>Link 2</Link>
              <Link>Link 3</Link>
            </div>
          </div>
        </div>

        <span style={{ fontSize: "1rem", marginLeft: "17.5%" }}>
          Netflix Türkiye
        </span>
      </FooterContainer>
    );
  }
}

const FooterContainer = styled.footer`
  background: var(--main-deep-dark);
  padding-top: 10rem;
  padding-bottom: 3rem;
  color: #999;

  ul li {
    list-style: none;
    line-height: 2.5;
  }

  a {
    color: #999 !important;
  }

  a:hover {
    text-decoration: underline !important;
    cursor: pointer;
  }

  .footer-columns {
    width: 70%;
    margin: 1rem auto 0;
    font-size: 0.9rem;
    overflow: auto;
    display: grid;
    grid-template-columns: repeat(4, 1fr);
  }

  .lang-btn {
    background: transparent;
    border: 0.9px solid #333;
    padding: 1rem;
    margin-left: 15%;
    width: 8rem;
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    margin-bottom: 2rem;
  }

  /////Toggle Language Content/////

  /* Dropup Button */
  .dropbtn {
    margin-bottom: 1rem;
    margin-left: 15%;
    background-color: transparent;
    color: white;
    padding: 1rem;
    font-size: 0.9rem;
    border: 1px solid #333;
  }

  /* The container <div> - needed to position the dropup content */
  .dropup {
    position: relative;
    display: inline-block;
  }

  /* Dropup content (Hidden by Default) */
  .dropup-content {
    color: #fff;
    display: none;
    position: absolute;
    bottom: 50px;
    background-color: var(--main-deep-dark);
    min-width: 160px;
    box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
    z-index: 1;
  }

  /* Links inside the dropup */
  .dropup-content a {
    color: #ffff;
    border: 1px solid #333;
    padding: 12px 16px;
    text-decoration: none;
    display: block;
  }

  /* Change color of dropup links on hover */
  .dropup-content a:hover {
    background-color: #0085ff;
  }

  /* Show the dropup menu on hover */
  .dropup:hover .dropup-content {
    display: block;
  }

  /* Change the background color of the dropup button when the dropup content is shown */
  .dropup:hover .dropbtn {
    background-color: transparent;
  }
`;

export default Footer;
