import React, { Component } from "react";
import styled from "styled-components";

import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

class Comments extends Component {
  static getInitialProps({ req }) {
    let userAgent;
    let deviceType;
    if (req) {
      userAgent = req.headers["user-agent"];
    } else {
      userAgent = navigator.userAgent;
    }
    const md = new MobileDetect(userAgent);
    if (md.tablet()) {
      deviceType = "tablet";
    } else if (md.mobile()) {
      deviceType = "mobile";
    } else {
      deviceType = "desktop";
    }
    return { deviceType };
  }

  state = { isMoving: false };

  render() {
    const { classes } = this.props;
    const fakerData = Array(12)
      .fill(0)
      .map((item, index) => {
        return {
          image: images[index],
          headline: "w3js -> web front-end studio",
          description: texts[index] || texts[0]
        };
      });
    const responsive = {
      desktop: {
        breakpoint: { max: 3000, min: 1024 },
        items: 3,
        slidesToSlide: 3
      },
      tablet: {
        breakpoint: { max: 1024, min: 464 },
        items: 2,
        slidesToSlide: 2
      },
      mobile: {
        breakpoint: { max: 464, min: 0 },
        items: 1,
        slidesToSlide: 1
      }
    };
    return (
      <Comment className="container-fluid">
        <div className="row">
          <div className="col-12 d-block ">
            <div className="ml-4 pl-4">
              <h4 className="mt-5">En Çok Sevilen Canlı Yayın Kanallar</h4>
            </div>
          </div>

          <div className={classes.root}>
            <Carousel
              /*
          swipeable={false}
          draggable={false}
          */
              responsive={responsive}
              ssr
              infinite={false}
              beforeChange={() => this.setState({ isMoving: true })}
              afterChange={() => this.setState({ isMoving: false })}
              containerClass="first-carousel-container container"
              deviceType={this.props.deviceType}
            >
              {fakerData.map(card => {
                return <Card isMoving={this.state.isMoving} {...card} />;
              })}
            </Carousel>

            <Carousel
              /*
          swipeable={false}
          draggable={false}
          */
              responsive={responsive}
              ssr
              showDots
              infinite
              containerClass="container-with-dots"
              itemClass="image-item"
              deviceType={this.props.deviceType}
            >
              {fakerData.slice(0, 5).map(card => {
                return <Image url={card.image} alt={card.headline} />;
              })}
            </Carousel>
            <CarouselWithCustomDots deviceType={this.props.deviceType} />
          </div>
          <div className="grid-container"></div>
        </div>
      </Comment>
    );
  }
}

const Comment = styled.div`
title: {
    maxWidth: 400,
    margin: "auto",
    marginTop: 10
  }
`;

export default Comments;
