import React, { Component } from "react";
import { Link } from "react-router-dom";
import Logo from "../logo/Logo";
import styled from "styled-components";

import Icon from "react-icons-kit";
import gif from "../images/video/ASK_I_MEMNU_45.mp4";
import { listAlt } from "react-icons-kit/fa/listAlt";

class PageNotFound extends Component {
  render() {
    return (
      <ErrorPage>
        <Headerstyle>
          <div className="header-top">
            <Logostyle>
              <Logo />
              <span className="signin">Üye Girişi</span>
            </Logostyle>
          </div>
        </Headerstyle>

        {/* Video */}

        <div className="playvideo">
          <div className="wrapper text-center">
            <Icon icon={listAlt} size={55} />
            <h2>Üzgünüz.. Aradığınız sayfayı bulamadık</h2>
            <Link to={"/"}> Ana sayfa </Link>
          </div>

          <video className="videoBG" autoPlay muted loop>
            <source src={gif} />
          </video>
        </div>
        {/* Footer */}
        <div className="Footer"></div>
      </ErrorPage>
    );
  }
}

const ErrorPage = styled.div`
  height: 100vh;

  .playvideo {
    display: flex;
    justify-content: center;
    align-items: center;
    background: rgba(36, 38, 48, 0.7);
    margin-bottom: 5rem;
    position: relative;
    overflow: hidden;
    min-width: 100%;
    min-height: 100%;
  }

  .videoBG {
    position: absolute;
    z-index: -1;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    min-height: 100%;
    min-height: 100%;
  }




  Link::after{position: absolute;
  content: '';
  top: 0;
  width: 1px;
  height: 100%;
  right: 10px;
  background: #42435B;}
}
`;

const Headerstyle = styled.div`
  background: #2e2f41;
  padding-bottom: 1rem;
`;

const Logostyle = styled.div`
  padding-top: 1em;
  margin-left: 5%;
  width: "auto";
  height: "auto";

  .signin {
    position: flex;
    float: right;
    padding-top: 0.5em;
    margin-right: 5%;
    font-size: 0.8rem;
    cursor: pointer;
    &:hover {
      text-decoration: underline;
    }
  }
`;

export default PageNotFound;
