import React, { Component } from "react";
import styled from "styled-components";
import logo from "../svg/logo.svg";
import { NavLink } from "react-router-dom";
import { Link } from "react-router-dom";

class Header extends Component {
  render() {
    return (
      <HeaderComponent className="header-container">
        <div className="header-top">
          <Logo src={logo} />
          <NavLink to="" className="signin-btn">
            Sign In
          </NavLink>
        </div>
        {/* Header Content */}
        <div className="header-content">
          <Title>See what's next</Title>
          <SubTitle>WATCH ANYWHERE. CANCEL ANYTIME.</SubTitle>
          <Link className="main-offer-btn">Try it now</Link>
        </div>
      </HeaderComponent>
    );
  }
}

//Logo
const Logo = styled.img`
  width: 10rem;
  height: 3.5rem;
  position: absolute;
  top: 25%;
  left: 50%;
  transorm: translate(-50%, -50%);
`;

//Header Container
const HeaderComponent = styled.div`
  .signin-btn {
    right: 0;
    margin: 1.125rem 3% 0;
    padding: 0.44rem 1.0625rem;
    font-wight: 400;
    line-height: normal;
    border-readius: 0.1875rem;
    font-sie: 1rem;
    background: var(--main-red);
    position: absolute;
    translate: transform(-50%, -50%);
    cursor: pointer;
    transtion: background 0.2rem ease-in-out;
  }
  .signin-btn:hover {
    text-decoration: none;
    background: var(--main-red);
  }

  //Header Top

  .header-top {
    position: relative;
    height: 10rem;
    z-index: 1;
  }

  //Header Content

  .header-content {
    width: 65%;
    position: relative;
    margin: 4.5rem auto 0;
    display: flex;
    justify-content:center,
    align-content:center;
    text-align:center;
    flex-direction:column;
    z-index:1
  }

  //Main Offer Button

  .main-offer-btn{
      display:inline-block;
      background:var(--main-red);
      text-transform:uppercase;
      border:none;
      outline:none;
      margin: 0 20%;
      padding:1.5rem;
      border-radius:0.18rem;
      font-size:2rem;
      text-align:center;
      box-shadow:0 1px 0 rgba(0,0,0,0.45);
      transition:background 0.2s ease-in;
      cursor:pointer;
  }
`;

//Main Title
const Title = styled.h1`
  margin: 0 0 1.2rem;
  font-size: 5rem;
  font-weight: 700;
  line-height: 1.1em;
`;

//Subtitle
const SubTitle = styled.h2`
  font-weight: 400;
  font-size: 1.875rem;
  line-height: 1.75rem;
  margin: 0 0 1.875rem;
  text-transform: uppercase;
`;

//Button

export default Header;
