import React, { Component } from "react";
import styled from "styled-components";

/* Images */
import Image1 from "../images/turkishseries/y1.png";
import Image2 from "../images/turkishseries/y2.png";
import Image3 from "../images/turkishseries/y3.png";
import Image4 from "../images/turkishseries/y4.png";

/* Serieses Logos */
import logo1 from "../images/logolar/vikingslogo.png";
import logo2 from "../images/logolar/handmaid.png";
import logo3 from "../images/logolar/shamelesslogo.png";
import logo4 from "../images/logolar/walkingdeadlogo.png";

import Icon from "react-icons-kit";
import { iosArrowThinRight } from "react-icons-kit/ionicons/iosArrowThinRight";
import { iosArrowDown } from "react-icons-kit/ionicons/iosArrowDown";

class ForeignSeries extends Component {
  render() {
    return (
      <Series className="container-fluid">
        <div className="section col-12">
          <h4>Blutv'nin En Sevilenleri</h4>
          <div className="grid-container">
            <div className="item">
              <img src={Image1} className="img" />
              <div className="itemlogo">
                <img src={logo1} className="imglogo" />
                <p>Tüm dünyada en çok izlenen dizilerden.</p>
              </div>

              <div className="itemexplore">
                <span>Keşfet</span>
                <Icon
                  icon={iosArrowThinRight}
                  size={35}
                  style={{
                    color: "#fff",
                    paddingLeft: "1rem"
                  }}
                />
              </div>
            </div>

            <div className="item">
              <img src={Image2} />
              <div className="itemlogo">
                <img src={logo2} />
                <p>14 dalda Emmy ve 2 Altın Küre ödüllü.</p>
              </div>

              <div className="itemexplore">
                <span>Keşfet</span>
                <Icon
                  icon={iosArrowThinRight}
                  size={35}
                  style={{
                    color: "#fff",
                    paddingLeft: "1rem"
                  }}
                />
              </div>
            </div>

            <div className="item">
              <img src={Image3} />
              <div className="itemlogo">
                <img src={logo3} />
                <p>Hayatı uçlarda yaşayan en çılgın aile.</p>
              </div>

              <div className="itemexplore">
                <span>Keşfet</span>
                <Icon
                  icon={iosArrowThinRight}
                  size={35}
                  style={{
                    color: "#fff",
                    paddingLeft: "1rem"
                  }}
                />
              </div>
            </div>

            <div className="item">
              <img src={Image4} />
              <div className="itemlogo">
                <img src={logo4} />
                <p>Yeni bölümleriyle FX kanal yayınından hemen sonra.</p>
              </div>

              <div className="itemexplore">
                <span>Keşfet</span>
                <Icon
                  icon={iosArrowThinRight}
                  size={35}
                  style={{
                    color: "#fff",
                    paddingLeft: "1rem"
                  }}
                />
              </div>
            </div>
          </div>
        </div>
        <div className="col-12 mt-5 d-flex justify-content-center">
          <div className="row">
            <div className="showmore">
              <span>Daha Fazlası</span>
              <Icon
                icon={iosArrowDown}
                size={25}
                style={{
                  color: "#fff",
                  paddingLeft: "0.5rem"
                }}
              />
            </div>
          </div>
        </div>
      </Series>
    );
  }
}

const Series = styled.div`
  padding: 1rem 3rem 3rem;

  h5 {
    font-family: Work Sans;
    font-size: 1.2rem;
    padding-bottom: 1rem;
  }

  .grid-container {
    display: grid;
    justify-content: center;
    grid-template-columns: auto auto auto auto;
    grid-gap: 1rem;
  }

  img {
    vertical-align: middle;
    width: 100%;
    border-radius: 0.2rem;
  }

  .imglogo:hover {
    cursor: pointer;
    border-radius: 0.2rem;
    background: rgba(255, 255, 255, 1);
    z-index: 3;
  }

  .itemlogo {
    position: absolute;
    bottom: 10rem;
    color: #fff;
    padding-left: 1rem;
    width: 13rem;
  }

  p {
    width: 18rem;
    position: absolute;
    color: #fff;
    padding-left: 0.2rem;
    padding-top: 1.6rem;
    opacity: 0.66;
    overflow: hidden;
    font-size: 1.2rem;
  }

  .itemexplore {
    position: absolute;
    bottom: 0;
    padding-bottom: 1.5rem;
    padding-left: 1.2rem;
    font-family: Work Sans;
    font-size: 1.4rem;
    font-weight: 600;
    align-items: center;
  }

  .showmore span {
    font-size: 1.28rem;
    font-weight: 400;
    cursor: pointer;
  }
`;

export default ForeignSeries;
