import React, { Component } from "react";
import { Link } from "react-router-dom";
import Logo from "../logo/Logo";
import styled from "styled-components";

import Icon from "react-icons-kit";
import gif from "../images/video/ASK_I_MEMNU_45.mp4";
import { listAlt } from "react-icons-kit/fa/listAlt";

class PageNotFound extends Component {
  render() {
    return (
      <ErrorPage>
        <Headerstyle>
          <div className="header-top">
            <Logostyle>
              <Logo />
              <span className="signin">Üye Girişi</span>
            </Logostyle>
          </div>
        </Headerstyle>

        {/* Video */}

        <div className="playvideo">
          <div className="wrapper text-center">
            <Icon icon={listAlt} size={55} />
            <h2>Üzgünüz.. Aradığınız sayfayı bulamadık</h2>
            <Link to={"/"}>
              <strong>Ana sayfa</strong>
            </Link>
          </div>

          <video className="videoBG" autoPlay muted loop>
            <source src={gif} />
          </video>
        </div>
        {/* Footer */}
        <div className="col-md-12 d-flex footer p-2 ">
          <Link className="footer-link">
            Kupon Kodu <span className="brackets">|</span>
          </Link>
          <Link className="footer-link">
            Gizlilik Politikası <span className="brackets">|</span>
          </Link>
          <Link className="footer-link">
            Aydınlatma İlkeleri <span className="brackets">|</span>
          </Link>
          {/* Icons */}
          <Link className="footer-link">
            <i className="fab fa-apple pr-2" />
            <div className="itemtext">App Store </div>
            <span className="brackets">|</span>
          </Link>
          <Link className="footer-link">
            <i class="fab fa-google-play pr-2" />
            <div className="itemtext">Google Play </div>
            <span className="brackets">|</span>
          </Link>
          {/* Social Media */}
          <Link className="footer-link">
            <i className="fab fa-facebook-square" />
          </Link>

          <Link className="footer-link">
            <i className="fab fa-twitter-square" />
          </Link>

          <Link className="footer-link">
            <i className="fab fa-instagram-square" />
          </Link>

          <Link className="footer-link">
            <i className="fab fa-youtube" />
            <span className="brackets">|</span>
          </Link>
          {/* Social Media End */}
          {/* Icons End*/}
          <Link className="footer-link">Yardım Merkezi</Link>
        </div>
      </ErrorPage>
    );
  }
}

const ErrorPage = styled.div`
  height: 100vh;

  .playvideo {
    display: flex;
    justify-content: center;
    align-items: center;
    background: rgba(36, 38, 48, 0.7);

    position: relative;
    overflow: hidden;
    min-width: 100%;
    min-height: 100%;
  }

  .videoBG {
    position: absolute;
    z-index: -1;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    min-height: 100%;
    min-height: 100%;
  }

  .wrapper {
    line-height: 5rem;
  }

  ////FOOTER////

  .footer-link {
    color: #fff !important;
    padding: 0.4rem;
    opacity: 70%;

    &:hover {
      opacity: 100%;
      text-decoration: none;
    }
  }

  .brackets {
    padding-left: 0.8rem;
    opacity: 20%;
  }

  ////MEDIA/////
  @media (min-width: 768px) {
    .footer-link {
      font-size: 0.7rem;
      display: flex;
    }
  }

  @media (max-width: 430px) {
    .footer-link {
      font-size: 0.7rem;
      display: flex;
    }
    .brackets {
      padding-left: 1rem;
    }

    .itemtext {
      display: none;
    }
  }
`;

const Headerstyle = styled.div`
  background: #2e2f41;
  padding-bottom: 1rem;
`;

const Logostyle = styled.div`
  padding-top: 1em;
  margin-left: 5%;
  width: "auto";
  height: "auto";

  .signin {
    position: flex;
    float: right;
    padding-top: 0.5em;
    margin-right: 5%;
    font-size: 0.8rem;
    cursor: pointer;
    &:hover {
      text-decoration: underline;
    }
  }
`;

export default PageNotFound;
