import React, { Component } from "react";
import styled from "styled-components";

/* Images */
import Image1 from "../images/turkishseries/r1.png";
import Image2 from "../images/turkishseries/r2.png";
import Image3 from "../images/turkishseries/r3.png";
import Image4 from "../images/turkishseries/r4.png";

/* Serieses Logos */
import logo1 from "../images/logolar/behzatc.png";
import logo2 from "../images/logolar/pavyon.png";
import logo3 from "../images/logolar/masum.png";
import logo4 from "../images/logolar/bozkir.png";

class SeriesTurkish extends Component {
  render() {
    return (
      <Series className="container">
        <div className="col-12">
          <h5>Özel Yapımlar ve Daha Fazlası</h5>
          <div className="grid-container">
            <div className="item1">
              <img src={Image1} />
              <div className="itemlogo">
                <img src={logo1} />
              </div>
            </div>

            <div className="item2">
              <img src={Image2} />
              <div className="itemlogo">
                <img src={logo2} />
              </div>
            </div>

            <div className="item3">
              <img src={Image3} />
              <div className="itemlogo">
                <img src={logo3} />
              </div>
            </div>

            <div className="item4">
              <img src={Image4} />
              <div className="itemlogo">
                <img src={logo4} />
              </div>
            </div>
          </div>
        </div>
      </Series>
    );
  }
}

const Series = styled.div`
  h5 {
    font-family: Work Sans;
    font-size: 1.15rem;
    padding-bottom: 1rem;
  }

  .grid-container {
    display: grid;
    justify-content: center;
    grid-template-columns: auto auto auto auto;
    grid-gap: 1rem;
  }

  img {
    vertical-align: middle;
    width: 100%;
  }

  .itemlogo {
    position: absolute;
    bottom: 5rem;
    color: #fff;
    padding-left: 1.1rem;
    width: 11rem;
  }
`;

export default SeriesTurkish;
