import React, { Component } from "react";
import styled from "styled-components";
import logo from "../svg/logo.svg";
import { NavLink } from "react-router-dom";

class Header extends Component {
  render() {
    return (
      <HeaderComponent className="header-container">
        <div className="header-top">
          <Logo src={logo} />
          <NavLink to="" className="signin-btn">
            Sign In
          </NavLink>
        </div>
        {/* Header Content */}
        <div className="header-content">
          <Title>See what's next</Title>
          <SubTitle>WATCH ANYWHERE. CANCEL ANYTIME.</SubTitle>
          <button>Try it now</button>
        </div>
      </HeaderComponent>
    );
  }
}

//Logo
const Logo = styled.img`
  width: 10rem;
  height: 3.5rem;
  position: absolute;
  top: 25%;
  left: 50%;
  transorm: translate(-50%, -50%);
`;

//Header Container
const HeaderComponent = styled.div`
  .signin-btn {
    right: 0;
    margin: 1.125rem 3% 0;
    padding: 0.44rem 1.0625rem;
    font-wight: 400;
    line-height: normal;
    border-readius: 0.1875rem;
    font-sie: 1rem;
    background: var(--main-red);
    position: absolute;
    translate: transform(-50%, -50%);
    cursor: pointer;
    transtion: background 0.2rem ease-in-out;
  }
  .signin-btn:hover {
    text-decoration: none;
    background: var(--main-red);
  }

  //Header Top

  .header-top {
    position: relative;
    height: 10rem;
    z-index: 1;
  }
`;

//Main Title
const Title = styled.h1`
  margin: 0 0 1.2rem;
  font-size: 5rem;
  font-weight: 700;
  line-height: 1.1rem;
`;

//Subtitle
const SubTitle = styled.h2`
  font-weight: 400;
  font-size: 1.875rem;
  line-height: 1.25rem;
  margin: 0 2rem 1.875rem;
  text-transform: uppercase;
`;

export default Header;
