import React, { Component } from "react";
import styled from "styled-components";

class SignIn extends Component {
  render() {
    return (
      <Account>
        <form className="form-container">
          <h5>Türkiye'nin Reklamsız İnternet Televizyonu Ayda Sadece ₺19,90</h5>
          <div className="input-container">
            <input type="text" placeholder="Ad Soyad" required />
          </div>
          <div className="input-container">
            <input type="email" placeholder="E-posta Adresiniz" required />
          </div>
          <div className="input-container">
            <input type="password" placeholder="Parola" required />
          </div>
          <div className="input-container">
            <button type="submit"> Hemen Üye Ol </button>
          </div>
        </form>
        <div className="row">
          <form>
            <div className="checkbox-container">
              <input type="checkbox" defaultChecked />
              <span>
                <strong>İletişim izinlerini</strong> onaylıyorum. | Üyelik
                oluşturduğunuzda <strong>üyelik sözleşmesini</strong>{" "}
                okuduğunuzu ve kabul ettiğinizi onaylıyorsunuz.
              </span>
            </div>
          </form>
        </div>
      </Account>
    );
  }
}

const Account = styled.div`
  width: 100vw;
  height: auto;
  padding: 20px 35px 5px 35px;
  margin-top: 1rem;
  position: relative;
  background: linear-gradient(
    94deg,
    rgba(60, 52, 107, 1),
    rgba(44, 72, 107, 1)
  );

  .form-container {
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    grid-gap: 0.2rem;
  }

  .row {
    display: flex;
    align-items: center;
  }

  span {
    font-size: 0.8rem;
  }
`;

export default SignIn;
